import React, { Component } from "react";
import axios from "axios";
import { Card, Header, Form, Input } from "semantic-ui-react";

let endpoint = "http://34.239.125.132:8080";

class WebsiteChecker extends Component {
  constructor(props) {
    super(props);

    this.state = {
      URL: "",
      checkInterval: "",
      websites: []
    };
  }

  componentDidMount() {
    this.getWebsites();
  }

  onChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  onSubmit = () => {
    let { URL, checkInterval } = this.state;
    // console.log("pRINTING task", this.state.task);
    if (URL && checkInterval) {
      axios
        .post(
          endpoint + "/register",
          {
            URL,
            checkInterval,
            method: "GET",
            expectedStatusCode: 200,
          },
          {
            headers: {
              "Content-Type": "application/json"
            }
          }
        )
        .then(res => {
          this.getWebsites();
          this.setState({
            URL: "",
            checkInterval: ""
          });
          console.log(res);
        });
    }else{
      alert("URL or Interval is missing");
    }
    
  };

  getWebsites = () => {
    axios.get(endpoint + "/websites").then(res => {
      console.log(res);
      // axios.get(endpoint + "/website/" + item["ID"]).then(statusRes => {});
      if (res.data) {
        this.setState({
          websites: res.data.map(item => {
            console.log(item)
            
            let color = "yellow";

            return (
              <Card key={item.ID} color={color} fluid>
                <Card.Content>
                  <Card.Header >
                    <div style={{ wordWrap: "break-word", textAlign:"left" }}>{item.URL}</div>
                    <div style={{ wordWrap: "break-word", textAlign: "right" }}>{item.checkInterval}</div>
                  </Card.Header>

                  {/* <Card.Meta textAlign="right">
                    <Icon
                      name="check circle"
                      color="green"
                      onClick={() => this.updateTask(item._id)}
                    />
                    <span style={{ paddingRight: 10 }}>Done</span>
                    <Icon
                      name="undo"
                      color="yellow"
                      onClick={() => this.undoTask(item._id)}
                    />
                    <span style={{ paddingRight: 10 }}>Undo</span>
                    <Icon
                      name="delete"
                      color="red"
                      onClick={() => this.deleteTask(item._id)}
                    />
                    <span style={{ paddingRight: 10 }}>Delete</span>
                  </Card.Meta> */}
                </Card.Content>
              </Card>
            );
          })
        });
      } else {
        this.setState({
          websites: []
        });
      }
    });
  };

  updateTask = id => {
    axios
      .put(endpoint + "/api/task/" + id, {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        }
      })
      .then(res => {
        console.log(res);
        this.getTask();
      });
  };

  undoTask = id => {
    axios
      .put(endpoint + "/api/undoTask/" + id, {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        }
      })
      .then(res => {
        console.log(res);
        this.getTask();
      });
  };

  deleteTask = id => {
    axios
      .delete(endpoint + "/api/deleteTask/" + id, {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        }
      })
      .then(res => {
        console.log(res);
        this.getTask();
      });
  };
  render() {
    return (
      <div>
        <div className="row">
          <Header className="header" as="h2">
            Website Health Checker
          </Header>
        </div>
        <div className="row">
          <Form onSubmit={this.onSubmit}>
            <Input
              type="text"
              name="URL"
              onChange={this.onChange}
              value={this.state.URL}
              fluid
              placeholder="Enter URL"
            />
            <Input
              type="text"
              name="interval(in sec)"
              onChange={this.onChange}
              value={this.state.checkInterval}
              fluid
              placeholder="Interval"
            />
            {/* <Button >Create Task</Button> */}
          </Form>
        </div>
        <div className="row">
          <Card.Group>{this.state.items}</Card.Group>
        </div>
      </div>
    );
  }
}

export default WebsiteChecker;
